mapper.select:
select count(id) from customer_integral_converts
and status=#{param1}
and apply_num like "%"#{param2}"%"


select id, quantity, name, phone,  status, created_at,  customer_id,apply_num
from customer_integral_converts


            and status=#{param2}


            and apply_num like "%"#{param3}"%"   


  order by created_at desc
    limit #{param1.offset}, #{param1.pageSize}
