  verify_user_id, verify_type, verified_at, 
  order_id, status, good_id, 
  created_at, updated_at, content
  )
values (#{id,jdbcType=INTEGER}, #{score,jdbcType=INTEGER}, #{customerId,jdbcType=INTEGER}, 
  #{verifyUserId,jdbcType=INTEGER}, #{verifyType,jdbcType=BIT}, #{verifiedAt,jdbcType=TIMESTAMP}, 
  #{orderId,jdbcType=INTEGER}, #{status,jdbcType=TINYINT}, #{goodId,jdbcType=INTEGER}, 
  #{createdAt,jdbcType=TIMESTAMP}, #{updatedAt,jdbcType=TIMESTAMP}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.select:
SELECT count(*) as count
FROM good_comments
WHERE status = #{status}
